SELECT PROJECT_PRODUCT_ID ,OU_CODE ,PROJECT_CODE ,PROJECT_VERSION ,BUSINESS_GROUP_CODE ,BUSINESS_GROUP_NAME ,PRODUCT_GROUP_CODE ,PRODUCT_GROUP_NAME ,PRODUCT_NAME_CODE ,PRODUCT_NAME ,PRODUCT_MODEL_CODE ,PRODUCT_MODEL_NAME ,PRODUCT_PKG_CODE ,PRODUCT_PKG_NAME ,ITEM_NAME ,ERP_ITEM_NO ,PSO_PROJECT_ID FROM OMS_PROJECT_PRODUCT_V PP WHERE OU_CODE = #{OU_CODE}
AND PP.PROJECT_CODE = #{PROJECT_CODE}
AND PP.PROJECT_VERSION = (SELECT PROJECT_VERSION FROM OMS_PROJECT OP WHERE OU_CODE = #{OU_CODE} AND OP.PROJECT_CODE = #{PROJECT_CODE} AND OP.LATEST_YN = 'Y')
AND PP.PROJECT_VERSION = #{PROJECT_VERSION}
ORDER BY BUSINESS_GROUP_CODE,PRODUCT_GROUP_NAME

