mapper.select:

		SELECT OU_CODE
		      ,PROJECT_CODE
		      ,PROJECT_PRODUCT_ID
		      ,BASE_YEAR
		      ,INCOTERMS_CODE
		      ,SMT_GET_COMMON_CODE_NAME_FUNC('O019', INCOTERMS_CODE, #{GV_LANG}) AS INCOTERMS_NAME
		      ,INCOTERMS_DETAIL
		      ,PLAN_VERSION
		      ,LATEST_YN
		      ,REMARK
		  FROM OMS_LONG_PLAN_HEADER_V
		 WHERE OU_CODE = #{OU_CODE}  
           AND PROJECT_PRODUCT_ID = #{PROJECT_PRODUCT_ID} 
           AND BASE_YEAR= #{BASE_YEAR}
           AND PROJECT_CODE   = #{PROJECT_CODE}
           AND PLAN_VERSION   = #{PLAN_VERSION}

SELECT OU_CODE ,PROJECT_CODE ,PROJECT_VERSION ,ACT_STATUS_CODE ,PROJECT_LATEST_YN ,PROJECT_PRODUCT_ID ,ITEM_NAME ,PLAN_VERSION ,BASE_YEAR ,PLAN_YEAR ,PLAN_MONTH ,PLAN_YEAR||PLAN_MONTH PLAN_YYYYMM ,QTY ,TO_CHAR(UNIT_PRICE, 'FM999999999990.0000') AS UNIT_PRICE ,CURRENCY_CODE ,SMT_GET_COMMON_CODE_NAME_FUNC('F018',CURRENCY_CODE, #{GV_LANG}) AS CURRENCY_NAME ,TO_CHAR(AMT, 'FM99999999999990.00') AS AMT ,TO_CHAR(AMT_KRW, 'FM9999999999999990') AS AMT ,REMARK ,LATEST_YN ,UPD_DATE ,UPDATE_USER_NAME FROM OMS_LONG_PLAN_V LP WHERE OU_CODE = #{OU_CODE}
AND LP.LATEST_YN = 'Y' --최신 버젼
AND LP.PROJECT_LATEST_YN =#{PROJECT_LATEST_YN}
AND LP.PROJECT_PRODUCT_ID = #{PROJECT_PRODUCT_ID}
AND LP.PLAN_VERSION = #{PLAN_VERSION}
AND LP.PROJECT_CODE like '%'|| #{PROJECT_CODE}|| '%'
AND LP.BASE_YEAR= #{BASE_YEAR}
AND LP.ACT_STATUS_CODE = #{ACT_STATUS_CODE}
ORDER BY TO_NUMBER (SUBSTR( LP.PROJECT_CODE,LENGTH(LP.PROJECT_CODE)-2) ),ITEM_NAME,PLAN_YEAR||PLAN_MONTH

SELECT OU_CODE ,PROJECT_CODE ,PROJECT_VERSION ,ACT_STATUS_CODE ,PROJECT_LATEST_YN ,PROJECT_PRODUCT_ID ,ITEM_NAME ,ERP_ITEM_NO ,INCOTERMS_CODE ,SMT_GET_COMMON_CODE_NAME_FUNC('O019',INCOTERMS_CODE, #{GV_LANG}) AS INCOTERMS_NAME ,INCOTERMS_DETAIL ,PLAN_VERSION ,BASE_YEAR ,PLAN_YEAR ,PLAN_MONTH ,PLAN_YEAR||PLAN_MONTH PLAN_YYYYMM ,QTY ,UNIT_PRICE ,CURRENCY_CODE ,SMT_GET_COMMON_CODE_NAME_FUNC('F018',CURRENCY_CODE, #{GV_LANG}) AS CURRENCY_NAME ,AMT ,AMT_KRW ,REMARK ,LATEST_YN ,DETAIL_STATUS_CODE ,UPD_DATE ,UPDATE_USER_NAME ,ROLLING_YN FROM OMS_LONG_PLAN_V LP WHERE OU_CODE = #{OU_CODE} AND NVL(DETAIL_STATUS_CODE,'-999') NOT IN ( 'D09','D04','D10') AND OMS_PROJECT_PKG.GEN_USER_AUTH_FUNC(#{GV_USER_ID}, OU_CODE, PROJECT_CODE) ='W'
AND LP.LATEST_YN = 'Y' --최신 버젼
AND LP.PROJECT_LATEST_YN =#{PROJECT_LATEST_YN}
AND LP.PROJECT_PRODUCT_ID = #{PROJECT_PRODUCT_ID}
AND LP.PLAN_VERSION = #{PLAN_VERSION}
AND LP.PROJECT_CODE like '%'|| #{PROJECT_CODE}|| '%'
AND LP.PROJECT_CODE like '%'|| #{RFQ_CODE}|| '%'
AND LP.BASE_YEAR= #{BASE_YEAR}
AND LP.ACT_STATUS_CODE = #{ACT_STATUS_CODE}
ORDER BY TO_NUMBER (SUBSTR( LP.PROJECT_CODE,LENGTH(LP.PROJECT_CODE)-2) ),ITEM_NAME,PROJECT_PRODUCT_ID,PLAN_YEAR||PLAN_MONTH


		SELECT  PLAN_VERSION AS CODE
		       ,PLAN_VERSION AS NAME
		       ,LATEST_YN AS LATEST_YN
		       ,UPD_DATE
		       ,UPDATE_USER_NAME
		  FROM OMS_LONG_PLAN_VERSION_V LP
		 WHERE OU_CODE = #{OU_CODE}
           AND LP.PROJECT_PRODUCT_ID = #{PROJECT_PRODUCT_ID} 
           AND LP.BASE_YEAR =  #{BASE_YEAR} 
  		 ORDER BY TO_NUMBER (PLAN_VERSION ) DESC

